import { useEffect, useState, useParams } from 'react';
import './InforFilm.scss';
import axios from 'axios';
function InforFilm(){
    const {id} = use
    const [inforfilm, setInforfilm] = useState(undefined);
    useEffect(() =>{
        axios.get('http://localhost:8081/AllInforMoviesId/1').
        then((res) => {
            if(res.data != undefined){
                setInforfilm(res.data);
            }
        })
        .catch((err) => console.log(err));
    },[]);
    console.log(inforfilm);
    if(inforfilm != undefined){
        const {year, director, actor, country,description} = inforfilm;
        return(
            <div className = {`container-sm w-75`}>
                <div className = "NameFilm">vo tien dat</div>
                <div className = "Page-infor">
                    <div className = "BackgroundFilm">
                        <img src = {`http://localhost:8081/static/images/fastandfurious1.jpg`} className = "BackgroundFilm-Image"/>
                        <ul className = "Control">
                            <li class=" Submit btn btn-danger opacity-100">
                                <a href = "/hello">Xem phim</a>
                            </li>
                        </ul>
                    </div>
                    <ul className = "List-Infor-Film">
                        <li>{`Đạo diễn:${director}`}</li>
                        <li>{`Diễn Viên: ${actor}`}</li>
                        <li>{`Nước Sản Xuât: ${country}`}</li>
                        <li>{`Năm Sản Xuất: ${year}`}</li>
                    </ul>
                </div>
                
                
                
                
                    
                <div className="ReviewFilm">
                    <div className = "">Review phim</div>
                    <div className = "">{description}</div>
                </div>
            </div>
        )
    }

    return(
        <h1> loading....</h1>
    )
    
}


export default InforFilm;
