import { useEffect, useState } from 'react';
import './InforFilm.scss';
import axios from 'axios';
function InforFilm(){
    const [inforfilm, setInforfilm] = useState(undefined);
    useEffect(() =>{
        axios.get('http://localhost:8081/AllInforMoviesId/1').
        then((res) => {
            if(res.data != undefined){
                setInforfilm(res.data);
            }
        })
        .catch((err) => console.log(err));
    },[]);
    console.log(inforfilm);
    if(inforfilm != undefined){
        const {year, director, actor, country,description} = inforfilm;
        return(
            <div className = {`container-sm w-75`}>
                <div className = "BackgroundFilm d-inline-block">
                    <img src = {`http://localhost:8081/static/images/fastandfurious1.jpg`}/>
                    <ul className = "h-25 opacity">
                        <li>
                            <button type="button" class="btn btn-danger">Danger</button>
                        </li>
                    </ul>
                </div>
                
                <div className = {`Name-Film`}></div>
                <ul className = "List-Infor-Film d-inline-block w-25">
                    <li>{`Đạo diễn:${director}`}</li>
                    <li>{`Diễn Viên: ${actor}`}</li>
                    <li>{`Nước Sản Xuât: ${country}`}</li>
                    <li>{`Năm Sản Xuất: ${year}`}</li>
                </ul>
                    
                <div className="container-sm w-100">
                    <div className = "">Review phim</div>
                    <div className = "">{description}</div>
                </div>
            </div>
        )
    }

    return(
        <h1> loading....</h1>
    )
    
}


export default InforFilm;
